import { useState, useEffect } from 'react'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'
import personsService from './services/persons'
import Notification from './components/Notification'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNumber] = useState('')
  const [filter, setFilter] = useState('')
  const [addPersonMessage, setAddPersonMessage] = useState(null);
  sSucess = false

  useEffect(() => {
    personsService
        .getAll()
        .then(response => {
          setPersons(response.data);
        });
  }, []);

  console.log('render', persons.length, 'persons')

  const handleNameChange = (event) => setNewName(event.target.value);
  const handleNumberChange = (event) => setNumber(event.target.value);
  const handleFilterChange = (event) => setFilter(event.target.value);

  const personsToShow = persons.filter(person => 
    person.name && person.name.toLowerCase().includes(filter.toLowerCase())
  );

  const resetForm = () => {
    setNewName('')
    setNumber('')
  }

  const displayNotification = (message) => {
    setAddPersonMessage(message);
    setTimeout(() => {
      setAddPersonMessage(null);
    }, 5000);
  }

  const addNewName = (event) => {
    event.preventDefault() 
    const newPerson = { name: newName, number: newNumber, id: persons.length+1 }
    if (checkSameName) {
      updatePerson(checkSameName.id, newPerson)
    } else {
      personsService
        .create(newPerson)
        .then(response => {
          setPersons(persons.concat(response.data));
          isSucess = true
          displayNotification(`Added ${newPerson.name}`);
          resetForm()
      });
    }
  }

  const checkSameName = persons.some(person => person.name === newName);

  const updatePerson = (id, newPerson) => {
    const existingPerson = persons.find(person => person.name === newPerson.name);
    if(window.confirm(`${newPerson.name} is already added to phonebook, replace the old number with a new one?`)) {
      personsService
        .update(existingPerson.id, newPerson)
        .then(response => {
          setPersons(persons.map(p => (p.id !== id ? p : response.data)));
          setPersons(persons.map(p => (p.id !== existingPerson.id ? p : response.data)));
            resetForm()
      })
      .catch(error => {
        isSucess = false
        displayNotification(`Error updating ${newPerson.name}. It might have been removed from the server.`)
        setPersons(persons.filter(p => p.id !== id));
      });
    }
  }

  const handleDeleteChange = (id) => {
    const person = persons.find(p => p.id === id)
    if(window.confirm(`Delete ${person.name}?`)) {
      personsService
      .deletePerson(id)
      .then(() => {
        setPersons(persons.filter(p => p.id !== id));
      })
      .catch(error => {
        isSucess = false
        displayNotification(`Information of ${person.name} has already been removed from server`)
        setPersons(persons.filter(p => p.id !== id));
      }); 
    }

  }

  return (
    <div>
      <h2>Phonebook</h2>
      {addPersonMessage && <Notification message={addPersonMessage} isSuccess={isSucess} />}
      <Filter filter={ filter } handleFilterChange={ handleFilterChange }/>
      <h2>Add a new</h2>
      <PersonForm 
        newName = { newName } 
        newNumber = { newNumber } 
        handleNameChange = { handleNameChange } 
        handleNumberChange = { handleNumberChange } 
        addNewName = { addNewName } 
      />
      <h2>Numbers</h2>
      <Persons persons={personsToShow} handleDelete={handleDeleteChange} />
    </div>
  )
}

export default App
